vector<Edge> e[MAXN];
bool inq[MAXN];

int rmq(int l, int r) {
	if(l > r)
		return 0;
	int d = Log[r - l + 1];
	return max(st[d][l], st[d][r - (1 << d) + 1]);
}

void dfs(int u, int fa) {
	f[u] = 0;
	for(auto it : e[u]) {
		int v = it.first, w = it.second;
		if(v == fa || inq[v])
			continue;
		dfs(v, u);
		if(f[u] < f[v] + w) {
			f[u] = f[v] + w;
			p[u] = v;
		}
	}
}

int main() {
	Log[1] = 0;
	for(int i = 2; i < MAXN; ++i)
		Log[i] = Log[i >> 1] + 1;
