		dis[tot] = f[p[u]];
		que[tot++] = p[u];
	}
	for(int i = 0; i < tot; ++i) {
		dfs(que[i], -1);
		pre[i] = suf[i] = st[0][i] = f[que[i]];
	}
	for(int i = 1; i < tot; ++i)
		if(pre[i] < pre[i - 1] + dis[i - 1] - dis[i])
			pre[i] = pre[i - 1] + dis[i - 1] - dis[i];
