vector<num> solve(string s){
	if(s.size() == 1){
		if(s == "a") return {1, 0, 0};
		if(s == "b") return {0, 0, 1};
		if(s == "?") return {1, 0, 1};
	}
	if(s[0] == '?'){
		string sa = s;
		string sb = s;
		sa[0] = 'a'; sb[0] = 'b';
		return add(solve(sa), solve(sb));
	}
	if(s[1] == '?'){
		string sa = s;
		string sb = s;
		sa[1] = 'a'; sb[1] = 'b';
		return add(solve(sa), solve(sb));
	}
	vector<num> ans(3, 0);
	auto pack = [&](tuple<int, array<int, 3>, array<int, 2>> state) -> int {
		auto [score, f, pc] = state;
		int a = score + 300;
		for(int x : f) a = 3 * a + x;
		for(int c : pc) a = 2 * a + c;
		return a;
	};
	auto unpack = [&](int a) -> tuple<int, array<int, 3>, array<int, 2>> {
		int score;
		array<int, 3> f;
		array<int, 2> pc;
		for(int i = 1; i >= 0; i--){
			pc[i] = a % 2;
			a /= 2;
		}
		for(int i = 2; i >= 0; i--){
			f[i] = a % 3;
			a /= 3;
		}
		score = a - 300;
		return {score, f, pc};
	};
	int C = 90000;
	for(int mask = 0; mask < 8; mask++){
		if(mask == 0) continue;
		vector<num> dp(C, 0);
		// [score] [f from 0, 1, 2] s[i-2] s[i-1]
		{
			int score = 0;
			int f0 = 0;
			if(s[0] == s[1]){
				f0 = 2;
				score += (s[0] == 'a' ? 1 : -1) * (((mask >> 0) & 1) ? 1 : 0);
			}
			dp[pack({score, {f0, 1, 2}, {s[0] - 'a', s[1] - 'a'}})] += 1;
		}
		for(int c = 2; c < (int)s.size(); c++){
			vector<num> ndp(C, 0);
			for(char x : {'a', 'b'}){
				int xi = x - 'a';
				if(s[c] != '?' && s[c] != x) continue;
				for(int state = 0; state < dp.size(); state++){
					num cnt = dp[state];
					if(cnt == 0) continue;
					auto [score, f, opc] = unpack(state);
					// array<int, 3> pc {opc[0], opc[1], x - 'a'};
					for(int b = 0; b < 3; b++){
						if(f[b] == 0){
							if(opc[0] == opc[1]){
								assert(false);
							} else {
								score += (xi == 0 ? 1 : -1) * (((mask >> b) & 1) ? 1 : 0);
								f[b] = 3;
							}
						} else if(f[b] == 1){
							if(opc[1] == xi){
								score += (xi == 0 ? 1 : -1) * (((mask >> b) & 1) ? 1 : 0);
								f[b] = 3;
							}
						}
						f[b]--;
					}
					ndp[pack({score, f, {opc[1], xi}})] += cnt;
				}
			}
			dp = ndp;
		}
		for(int state = 0; state < dp.size(); state++){
			num cnt = dp[state];
			if(cnt == 0) continue;
			auto [score, f, pc] = unpack(state);
			vector<int> go(3);
			for(int b = 0; b < 3; b++){
				if(f[b] == 2){
					go[b] = 0;
				} else {
					vector<int> cur;
					for(int x = f[b]; x < 2; x++){
						cur.push_back(pc[x]);
					}
					int i = 0;
					while(true){
						cur.push_back(s[i] - 'a');
						i++;
						if(cur.size() >= 3 || cur[0] == cur[1]){
							break;
						}
					}
					score += (cur.back() == 0 ? 1 : -1) * (((mask >> b) & 1) ? 1 : 0);
					if(s.size() == 2 && i == 2) i = 0;
					go[b] = i;
				}
			}
			int r = go[go[go[go[0]]]];
			if(!((mask >> r) & 1)) continue;
			int c = r;
			int real_mask = 0;
			while(true){
				real_mask ^= (1 << c);
				c = go[c];
				if(c == r) break;
			}
			if(real_mask != mask) continue;
			int which;
			if(score > 0){
				which = 0;
			} else if(score == 0){
				which = 1;
			} else if(score < 0){
				which = 2;
			} else assert(false);
			ans[which] += cnt;
		}
	}
	return ans;
}

int main(){
	ios_base::sync_with_stdio(false), cin.tie(nullptr);
	string s;
	cin >> s;
	vector<num> ans = solve(s);
	for(num x : ans) cout << x << '\n';
