    std::vector<int> f(n, -1);
    std::priority_queue<std::pair<int, int>> h;

    std::set<int> jump, stones;
    for (int i = 0; i < n; i++) {
        stones.insert(i);
    }

    auto getK = [&](int u) {
        int x = a[u];
        auto it = jump.lower_bound(x);

        int ans = 1e9;
        if (it != jump.end()) {
            ans = std::min(ans, *it - x);
        }
        if (it != jump.begin()) {
            ans = std::min(ans, x - *std::prev(it));
        }

        return ans;
    };

