				qsmall[cr].pop_back();
				ans[id] = slow[l[id] ];
			}
		}

		for (int i = be - 1; i >= bs; i--)
		{
			slow[i] = slow[i + 1];
			for (int j = i + 1; j <= be; j++)
				slow[i] = min(slow[i], abs(as[a[i] ] - as[a[j] ] ) );
		}

		int ptr = 0;
		int mnR = as[k - 1] - as[0] + 1;
		for (int i = be + 1; i <= n; i++)
		{
			while (!listD[i].empty() && listD[i].back() <= be)
