                s -= (x % y) * n;
                std::vector<int> dp(s + 1, 1e9);
                std::vector<int> par(s + 1, -1);
                dp[0] = 0;
                for (int i = 0; i <= s; ++i) {
                        for (int j = 1; i + j * (j - 1) / 2 * y <= s; ++j) {
                                int k = i + j * (j - 1) / 2 * y;
                                if (dp[k] > dp[i] + j) {
                                        dp[k] = dp[i] + j;
                                        par[k] = j;
                                }
                        }
                }
                bool ok = false;
                for (int d = 1; (2 * st + d - 1) * d / 2 * y <= s; ++d) {
                        if (d + dp[s - (2 * st + d - 1) * d / 2 * y] <= n) {
