  vector<vector<int>> table;
  vector<int> logs, array;

  STrmq(const vector<int>& a) {
    if (a.size() == 0) return;

    array = a;
    int lg = 0;
    while ((1<<lg) <= (int)a.size()) lg++;
    table = vector<vector<int>>(a.size(), vector<int>(lg));

    REP(i, (int)a.size()) table[i][0] = i;
    FOR(j, 1, lg) REP(i, (int)a.size()) {
