        nxt[x][s[j] - 'a'] = ++tot;
        val[tot] = s[j];
        pr[tot] = x;
      }
      x = nxt[x][s[j] - 'a'];
    }
  }
  vector<int> order;
  function<void(int)> dfs = [&](int v) {
    for (int i = 0; i < 26; ++i) {
