struct dsu {
    vector<int> p, s;
    dsu(int n) {
        p.resize(n);
        iota(all(p), 0);
        s.assign(n, 1);
    }
    int get(int v) {
        if (v == p[v]) return v;
        p[v] = get(p[v]);
        return p[v];
    }
    int unite(int u, int v) {
        u = get(u), v = get(v);
        if (u == v) return 0;
        if (s[u] < s[v]) swap(u, v);
        int res = 0;
        res -= s[u] * (s[u] - 1) / 2;
        res -= s[v] * (s[v] - 1) / 2;
        s[u] += s[v];
        p[v] = u;
        res += s[u] * (s[u] - 1) / 2;
        res--;
        return res;
    }
};

