V<Info> conv(const V<Info> &dp_l, const V<Info> &dp_r) {
	// dbg("COMPUTING", sz(dp_l), sz(dp_r));
	int max_ops = min(dp_l.bk.k + dp_r.bk.k, K);
	V<vpi> cands(max_ops + 1);
	F0R(i, sz(dp_l)) F0R(j, sz(dp_r)) if (dp_l[i].k + dp_r[j].k <= K) {
		cands[dp_l[i].k + dp_r[j].k].pb({i, j});
	}
	auto better_pair = [&](pi a, pi b) {
		if (a.f <= b.f && a.s <= b.s) return b;
		swap(a, b);
		if (a.f <= b.f && a.s <= b.s) return b;
		if (a.f > b.f) swap(a, b);
		assert(a.f < b.f && a.s > b.s);
		int min_dif_a = INT_MAX;
		FOR(i, a.f + 1, b.f + 1) ckmin(min_dif_a, dp_l.at(i).first_different);
		int min_dif_b = INT_MAX;
		FOR(i, b.s + 1, a.s + 1) ckmin(min_dif_b, dp_r.at(i).first_different);
		if (min_dif_a < min_dif_b) return b;
		return a;
	};
	auto first_different = [&](pi a, pi b) {
		int min_dif = INT_MAX;
		FOR(i, min(a.f, b.f) + 1, max(a.f, b.f) + 1)
		ckmin(min_dif, dp_l.at(i).first_different);
		FOR(i, min(a.s, b.s) + 1, max(a.s, b.s) + 1)
		ckmin(min_dif, dp_r.at(i).first_different);
		return min_dif;
	};
	vpi best;
	F0R(i, sz(cands)) {
		while (sz(cands[i]) > 1) {
			end(cands[i])[-2] =
			    better_pair(end(cands[i])[-2], end(cands[i])[-1]);
			cands[i].pop_back();
		}
		if (sz(cands[i])) {
			if (sz(best) && better_pair(best.bk, cands[i].bk) == best.bk)
				continue;
			best.pb(cands[i].bk);
			// ret.pb();
		}
	}
	V<Info> ndp;
	F0R(i, sz(best)) {
		auto [l, r] = best[i];
		ndp.pb({dp_l[l].k + dp_r[r].k,
		        i == 0 ? INT_MAX : first_different(best.at(i - 1), best.at(i)),
		        {l, r},
		        dp_l[l].r + dp_r[r].r});
	}
	return ndp;
	// V<Info> ndp;
