using namespace std;

const int MAX_N = int(1e5) + 10;

struct TA {
	vector<int> a;
	int n;

	void init(int n) {
		this->n = n;
		a.assign(n, 0);
	}
	void add(int p, int x) {
		for (p++; p <= n; p += p & -p)
			a[p - 1] += x;
	}
	int get(int p) {
		int r = 0;
		for (p++; p; p -= p & -p)
			r += a[p - 1];
		return r;
	}
};

struct Data {
	TA ta;
	vector<int> a;
	int n, tot;

	Data() {
		n = 1;
		a.resize(n);
		ta.init(n);
		tot = 0;
	}

	void grow() {
		ta.init(2 * n);
		for (int i = 0; i < n; ++i) {
			ta.add(i, a[i]);
		}
		a.resize(2 * n);
		n <<= 1;
	}

	void add(int p, int x) {
		while (p >= n)
			grow();
		ta.add(p, x), tot += x;
		a[p] += x;
	}

	int get(int p) { //<=p
		p = min(p, n - 1);
		return ta.get(p);
	}
};

int n, m;
int c[MAX_N];
vector<int> E[MAX_N];

vector<pair<int, int> > by[MAX_N];
int ans[MAX_N];
int L[MAX_N], R[MAX_N], seq[MAX_N], pt;
vector<int> color[MAX_N];

int size[MAX_N];

void dfs1(int u, int par) {
	L[u] = pt, seq[pt++] = u, size[u] = 1;
	for (vector<int>::iterator e = E[u].begin(); e != E[u].end(); ++e)
		if (*e != par) {
			dfs1(*e, u);
			size[u] += size[*e];
		}
	R[u] = pt;
}

int mask[MAX_N], cmask;

int calc(int v, int L, int R) { //v occur in [L,R)
	vector<int>&arr = color[v];
	return lower_bound(arr.begin(), arr.end(), R)
			- lower_bound(arr.begin(), arr.end(), L);
}

void update(Data*me, int v, int L, int R, int pL, int pR) {
	if (mask[v] == cmask)
		return;
	mask[v] = cmask;
	int old = calc(v, pL, pR), now = calc(v, L, R);
	if (old == now)
		return;
	if (old == 0) {
		me->add(now, 1);
	} else {
		me->add(old, -1);
		me->add(now, 1);
	}
}

Data*dfs2(int u, int par) {
	int v = -1;
	Data*me = 0;

	for (vector<int>::iterator e = E[u].begin(); e != E[u].end(); ++e)
		if (*e != par) {
			Data*c = dfs2(*e, u);
			if (v == -1 || size[*e] > size[v]) {
				if (me != 0)
					delete me;
				v = *e, me = c;
			} else
				delete c;
		}

	if (me == 0) {
		me = new Data;
		me->add(1, 1);
	} else {
		//update it!
		++cmask;
		int L = ::L[u], R = ::R[u];
		int pL = ::L[v], pR = ::R[v];

		for (int i = L; i < pL; ++i) {
			update(me, c[seq[i]], L, R, pL, pR);
		}

		for (int i = pR; i < R; ++i) {
			update(me, c[seq[i]], L, R, pL, pR);
		}
	}

	for (vector<pair<int, int> >::iterator e = by[u].begin(); e != by[u].end();
			++e) {
		ans[e->second] = me->tot - me->get(e->first - 1);
	}
	return me;
}

int main() {
