	}
	for (int i = 0; i < n - 1; ++i) {
		int a, b;
		scanf("%d%d", &a, &b), --a, --b;
		E[a].push_back(b), E[b].push_back(a);
	}

	for (int i = 0; i < m; ++i) {
		int v, k;
		scanf("%d%d", &v, &k), --v;
		by[v].push_back(make_pair(k, i));
	}

	dfs1(0, -1);
	for (int i = 0; i < n; ++i) {
		int u = seq[i];
		color[c[u]].push_back(i);
	}

	Data*me = dfs2(0, -1);
//	for (int i = 0; i < me->a.size(); ++i) {
