ll ind(ll a, ll b) {
	ll num = a-b;
	// 1..K -> 1..K
	// K+1..2K -> K+2..2K+1
	ll rem = num%K; if (rem == 0) rem = K;
	return (num-1)/K*(K+1)+rem;
	// there are n-x.s, groups of K
}

ll range(ll L, ll R) {
	return (L+R)*(R-L+1)/2;
}

ll range(ll L, ll R, ll X) {
	if (X < L) return range(L,R);
	if (X > R) return range(L-1,R-1);
	return range(L-1,R)-X;
}

pl lastSum(ll n) {
	if (n < K*(K+1)/2) return {0,0};
	// x, x-2, ..., x-K <= n -> K*x <= n+K*(K+1)/2-1
	ll x = (n+K*(K+1)/2-1)/K; auto a = lastSum(x);
	if (a.f == x) {
		ll xind = ind(x-1,a.s-1); int pos = xind%(K+1);
		if (pos == K) return {range(x-K,x-1),xind/(K+1)+1};
		return {range(x-pos-K,x-pos-1),xind/(K+1)};
	} else {
		ll xind = ind(x,a.s); int pos = xind%(K+1);
		if (range(x-pos+1,x-pos+K,a.f) <= n) return {range(x-pos+1,x-pos+K,a.f),xind/(K+1)+1};
		return {range(x-pos+1-K,x-pos,a.f),xind/(K+1)};
	}
}

