// given binary integers a[i]
// minimize  \sum_i b[i]
// subject to  b[i] >= a[i]  &&  b[i] \cap b[j] = 0
struct AdditionWithoutCarry {
  string ans;
  // (pos, next largest)
  vector<pair<int, int>> ones;
  // u: index in ones -> index in seg
  vector<int> ord;

  void addBigEndian(string s) {
    const int len = s.size();
    int last = -1;
    for (int x = 0; x < len; ++x) if (s[len - 1 - x] == '1') {
      ones.emplace_back(x, last);
      last = (int)ones.size() - 1;
    }
  }
  template <class T> void addInt(T a) {
    int last = -1;
    for (int x = 0; a; ++x) {
      if (a >> x & 1) {
        ones.emplace_back(x, last);
        last = (int)ones.size() - 1;
        a &= a - 1;
      }
    }
  }

  // max (pos + (index from right))
  struct Node {
    int mx, num, id;
    Node() : mx(-1), num(0), id(-1) {}
    Node(int val, int id_) : mx(val), num(1), id(id_) {}
    void merge(const Node &l, const Node &r) {
      if (l.mx + r.num <= r.mx) {
        mx = r.mx;
        id = r.id;
      } else {
        mx = l.mx + r.num;
        id = l.id;
      }
      num = l.num + r.num;
    }
  };
  SegmentTreePoint<Node> seg;
  bool rec(int used, int r) {
    const auto res = seg.get(0, r);
    const int u = res.id;
    if (!~u) return true;
    const int x = ones[u].first;
    const int k = seg.get(ord[u], r).num;
    // try to assign 1 for [x, x + k)
    if (x + k <= used) {
      const int v = ones[u].second;
      if (~v) seg.change(ord[v], Node(ones[v].first, v));
      if (rec(x, ord[u])) {
        fill(ans.begin() + x, ans.begin() + (x + k), '1');
        return true;
      }
      if (~v) seg.change(ord[v], Node());
    }
    // try to assign 1 for [x + 1, x + 1 + k)
    if (x + 1 + k <= used) {
      if (rec(x + 1, ord[u])) {
        fill(ans.begin() + (x + 1), ans.begin() + (x + 1 + k), '1');
        return true;
      }
    }
    return false;
  }
  void run() {
    if (ones.empty()) {
      // might need "0"
      ans = "";
      return;
    }
    const int onesLen = ones.size();
    int limX = 0;
    for (int u = 0; u < onesLen; ++u) chmax(limX, ones[u].first + 1);
    vector<vector<int>> uss(limX);
    for (int u = 0; u < onesLen; ++u) uss[ones[u].first].push_back(u);
    ord.assign(onesLen, -1);
    {
      int id = 0;
      for (int j = 0; j < limX; ++j) {
        sort(uss[j].begin(), uss[j].end(), [&](int u, int v) -> bool {
          return (((~ones[u].second) ? ord[ones[u].second] : -1) < 
                  ((~ones[v].second) ? ord[ones[v].second] : -1));
        });
        for (const int u : uss[j]) ord[u] = id++;
      }
    }
    seg = SegmentTreePoint<Node>(onesLen);
    for (int u = 0; u < onesLen; ++u) {
      if (~ones[u].second) seg.at(ord[ones[u].second]) = Node();
      seg.at(ord[u]) = Node(ones[u].first, u);
    }
    seg.build();
    ans.assign(seg.ts[1].mx + 2, '0');
    rec(seg.ts[1].mx + 2, onesLen);
    if (ans.back() == '0') ans.pop_back();
    assert(!ans.empty());
    assert(ans.back() == '1');
  }
};


int N;
vector<int> A, B;

int Ask(int i) {
  printf("? %d %d\n", A[i] + 1, B[i] + 1);
  fflush(stdout);
  int u;
