}

vector<vector<int>> G;
vector<int> del;

vector<__int128> dp;
int neck, cut;
void dfs(int u, int pi) {
  for (const int i : G[u]) if (!del[i] && pi != i) {
    const int v = A[i] ^ B[i] ^ u;
    dfs(v, i);
  }
  __int128 mx = -1;
  int im = -1;
  AdditionWithoutCarry f;
  for (const int i : G[u]) if (!del[i] && pi != i) {
    const int v = A[i] ^ B[i] ^ u;
    if (chmax(mx, dp[v])) {
      im = i;
    }
    f.addInt(dp[v] + 1);
  }
  f.run();
  dp[u] = 0;
  for (int x = 0; x < (int)f.ans.size(); ++x) if (f.ans[x] == '1') {
    dp[u] |= (__int128)1 << x;
    if (chmax(neck, x)) {
      cut = im;
    }
  }
}

