    G[A[i]].push_back(i);
    G[B[i]].push_back(i);
  }
  del.assign(N - 1, 0);

  for (int r = 0; ; ) {
    neck = -1;
    cut = -1;
    dp.assign(N, -1);
    dfs(r, -1);
// cerr<<"dp = "<<dp<<endl;
    if (~cut) {
      del[cut] = 1;
      r = Ask(cut);
    } else {
      Answer(r);
      break;
    }
  }

  return 0;
}
