['#include <bits/stdc++.h>

using i64 = long long;
template<class Info, class Tag>
struct LazySegmentTree {
    const int n;
    std::vector<Info> info;
    std::vector<Tag> tag;
    LazySegmentTree(int n) : n(n), info(4 << std::__lg(n)), tag(4 << std::__lg(n)) {}
    LazySegmentTree(std::vector<Info> init) : LazySegmentTree(init.size()) {
        std::function<void(int, int, int)> build = [&](int p, int l, int r) {
            if (r - l == 1) {
                info[p] = init[l];
                return;
            }
            int m = (l + r) / 2;
            build(2 * p, l, m);
            build(2 * p + 1, m, r);
            pull(p);
        };
        build(1, 0, n);
    }
    void pull(int p) {
        info[p] = info[2 * p] + info[2 * p + 1];
    }
    void apply(int p, const Tag &v) {
        info[p].apply(v);
        tag[p].apply(v);
    }
    void push(int p) {
        apply(2 * p, tag[p]);
        apply(2 * p + 1, tag[p]);
        tag[p] = Tag();
    }
    void modify(int p, int l, int r, int x, const Info &v) {
        if (r - l == 1) {
            info[p] = v;
            return;
        }
        int m = (l + r) / 2;
        push(p);
        if (x < m) {
            modify(2 * p, l, m, x, v);
        } else {
            modify(2 * p + 1, m, r, x, v);
        }
        pull(p);
    }
    void modify(int p, const Info &v) {
        modify(1, 0, n, p, v);
    }
    Info rangeQuery(int p, int l, int r, int x, int y) {
        if (l >= y || r <= x) {
            return Info();
        }
        if (l >= x && r <= y) {
            return info[p];
        }
        int m = (l + r) / 2;
        push(p);
        return rangeQuery(2 * p, l, m, x, y) + rangeQuery(2 * p + 1, m, r, x, y);
    }
    Info rangeQuery(int l, int r) {
        return rangeQuery(1, 0, n, l, r);
    }
    void rangeApply(int p, int l, int r, int x, int y, const Tag &v) {
        if (l >= y || r <= x) {
            return;
        }
        if (l >= x && r <= y) {
            apply(p, v);
            return;
        }
        int m = (l + r) / 2;
        push(p);
        rangeApply(2 * p, l, m, x, y, v);
        rangeApply(2 * p + 1, m, r, x, y, v);
        pull(p);
    }
    void rangeApply(int l, int r, const Tag &v) {
        return rangeApply(1, 0, n, l, r, v);
    }
    void half(int p, int l, int r) {
        if (info[p].act == 0) {
            return;
        }
        if ((info[p].min + 1) / 2 == (info[p].max + 1) / 2) {
            apply(p, {-(info[p].min + 1) / 2});
            return;
        }
        int m = (l + r) / 2;
        push(p);
        half(2 * p, l, m);
        half(2 * p + 1, m, r);
        pull(p);
    }
    void half() {
        half(1, 0, n);
    }
};

constexpr i64 inf = 1E18;

struct Tag {
    i64 add = 0;

    void apply(Tag t) {
        add += t.add;
    }
};

struct Info {
    i64 min = inf;
    i64 max = -inf;
    i64 sum = 0;
    i64 act = 0;

    void apply(Tag t) {
        min += t.add;
        max += t.add;
        sum += act * t.add;
    }
};

Info operator+(Info a, Info b) {
    Info c;
    c.min = std::min(a.min, b.min);
    c.max = std::max(a.max, b.max);
    c.sum = a.sum + b.sum;
    c.act = a.act + b.act;
    return c;
}

int main() {
    std::ios::sync_with_stdio(false);
    std::cin.tie(nullptr);

    int n, b;
    std::cin >> n >> b;

    i64 ans = 0;

    std::vector<std::array<int, 3>> events;
    for (int i = 0; i < n; i++) {
        int s, f, d;
        std::cin >> s >> f >> d;
        s--;
        events.push_back({s, i, d});
        events.push_back({f, i, 0});
    }

    std::sort(events.begin(), events.end());

    LazySegmentTree<Info, Tag> seg(n);
    int cur = 0;
    for (auto [t, i, d] : events) {
        Info last = {-1, -1, -1, -1};
        i64 lastc = -1;
        i64 lastans = -1;
        while (cur < t) {
            auto info = seg.rangeQuery(0, n);
            if (info.act == 0) {
                cur = t;
                break;
            }
            if (info.min == last.min && info.max == last.max && info.sum == last.sum && info.max - info.min <= 1) {
                i64 dt = cur - lastc;
                i64 dans = ans - lastans;
                i64 phase = (t - cur) / dt;
                cur += phase * dt;
                ans += phase * dans;
            }
            last = info;
            lastc = cur;
            lastans = ans;
            i64 u = (b - info.sum + info.act) / info.act;
            if (u > t - cur) {
                u = t - cur;
            }
            if (u < 0) {
                u = 0;
            }
            cur += u;
            ans += (info.sum + info.sum + info.act * (u - 1)) * u / 2;
            seg.rangeApply(0, n, {u});
            if (t == cur) {
                break;
            }
            cur += 1;
            seg.half();
        }
        if (d != 0) {
            seg.modify(i, {d, d, d, 1});
        } else {
            seg.modify(i, Info());
        }
    }
    std::cout << ans << "\n";

    return 0;
}
