void run (int x)
{
    for (int i = 2; i * i <= x; i++)
    {
        if (x % i == 0)
            pfac.insert(i);
        while (x % i == 0)
            x /= i;
    }
    if (x > 1)
        pfac.insert(x);
}

ll solve (int x)
{
    int mlo = N, mhi = -1;
    for (int i = 0; i < N; i++)
    {
        int k = (arr[i] + 1) % x;
        if (k == 1) tval[i] = 0;
        else if (k == 0 || k == 2) tval[i] = 1;
        else
        {
            mlo = min (mlo, i);
            mhi = max (mhi, i);
        }
    }

    ll tot = 0;
    for (int i = 0; i < N; i++)
    {
        if (i >= mlo && i <= mhi)
            tot += A;
        else tot += B * tval[i];
    }

    if (mlo < N)
    {
        ll bhi = 0, btot = 0;
        for (int i = mhi + 1; i < N; i++)
        {
            btot += A - tval[i] * B;
            bhi = min (bhi, btot);
        }
        ll blo = 0;
        btot = 0;
        for (int i = mlo - 1; i >= 0; i--)
        {
