using ll = int64_t;

using S = ll;
using F = ll;

S op(S x, S y) { return min(x, y); }
S e(){ return ll(1e9); }
S mapping(F f, S x) { return f + x; }
F composition(F f, F g) { return f + g; }
F id() { return 0; }

template<class T>
using ordered_set = tree<T, null_type, less<T>, rb_tree_tag,
    tree_order_statistics_node_update>;

void solve(){
	int N;
	cin >> N;
	vector<ll> A(N);
	for(ll& x : A) cin >> x;
	vector<int> ord(N);
	for(int i = 0; i < N; i++) ord[i] = i;
	sort(ord.begin(), ord.end(), [&](int x, int y) { return A[x] < A[y]; });
	vector<int> inv_ord(N);
	for(int i = 0; i < N; i++) inv_ord[ord[i]] = i;
	atcoder::lazy_segtree<S, op, e, F, mapping, composition, id> seg(N);
	ll cnt = 0;
	ll in_seg_sum = 0;

	ordered_set<int> in_set_idx;
	for(int i = 0; i < N; i++){
		int loc = inv_ord[i];

		int nbefore = in_set_idx.order_of_key(loc);
		in_set_idx.insert(loc);
		seg.set(loc, A[i] - nbefore - 1);
		seg.apply(loc+1, N, -1);
		cnt += 1;
		in_seg_sum += A[i];
		int f = seg.max_right(0, [&](S x) -> bool {
			return x >= 0;
		});
		if(f < N){
			assert(seg.get(f) < 0);
			seg.set(f, e());
			seg.apply(f+1, N, 1);
			in_set_idx.erase(f);
			cnt -= 1;
			in_seg_sum -= A[ord[f]];
		}
		ll ans = in_seg_sum - ll(cnt) * ll(cnt + 1) / 2;
		cout << ans << " \n"[i == N-1];
	}
}

int main(){
	ios_base::sync_with_stdio(false), cin.tie(nullptr);
	int T;
	cin >> T;
	while(T--) solve();
