#define foreach(c,itr) for(__typeof((c).begin()) itr=(c).begin();itr!=(c).end();itr++)

typedef long long ll;

ll power(ll x, ll n, ll p){
    if(n == 0) return 1;
    ll y = power(x, n/2, p);
    y = y * y % p;
    if(n % 2 == 1) y = y * x % p;
    return y;
}

ll power2(ll x, ll n, ll p){ // (x ^ (2 ^ n)) mod p
    if(x % p == 0) return 0;
    ll tmp = power(2, n, p-1);
    return power(x, tmp, p);
}

ll func(ll x, ll l, ll r, ll p){
    int i;

    if(p == 2){
        if(x%2 == 0) return 1;
        return 0;
    }

    ll y = power2(x, l, p);

    ll z;
    if(y == 1){
        z = power(2, r+1-l, p);
    } else {
        ll a = power2(x, r+1, p);
        a = (a + p - 1) % p;
        ll b = y;
        b = (b + p - 1) % p;
        z = a * power(b, p-2, p) % p;
    }

    if(x % 2 == 0) return z;

    ll ord_sum = r - l + 1;
    ll max_ord = 1;

    if(l == 0){
        ll tmp = x + 1;
        max_ord = 0;
        while(tmp % 2 == 0){
            tmp /= 2;
            max_ord++;
        }
        ord_sum += max_ord - 1;
    }

    ord_sum -= max_ord;
    ll two = power(2, ord_sum, p);
    z = z * power(two, p-2, p) % p;

    return z;
}

