	}
	void add(int id, LL val)
	{
		sum += val;
		for(; id <= B; id += id & -id) dat[id] += val;
	}
	LL query(int id)
	{
		LL ret = 0;
		for(; id > 0; id -= id & -id) ret += dat[id];
