void dfs(int v, int x) {
  Reachable[v][x] = true;
  for (int s : Adj[v])
    if (!Reachable[s][x ^ 1]) {
      PrevVert[s][x ^ 1] = v;
      dfs(s, x ^ 1);
    }
}

int Vis[MaxN];

bool dfsCycle(int v) {
  Vis[v] = 1;
  for (int s : Adj[v]) {
    if (Vis[s] == 1) { return true; }
    if (Vis[s] == 0 && dfsCycle(s))
      return true;
  }

  Vis[v] = 2;
  return false;
}


