		mod(pos + pos + 1, (l + r) / 2 + 1, r, l, r, add[pos]);
		add[pos] = 0;
	}
	long long q1 = fin(pos + pos, l, (l + r) / 2, ll, rr);
	long long q2 = fin(pos + pos + 1, (l + r) / 2 + 1, r, ll, rr);
	return q1 + q2;
}

