void build(int x, int l, int r) {
  max[x] = add[x] = 0;
  index[x] = l;
  if (l < r) {
    int y = (l + r) >> 1;
    build(x + x, l, y);
    build(x + x + 1, y + 1, r);
  }
}

void modify(int x, int l, int r, int ll, int rr, int v) {
  if (r < ll || rr < l) return;
  if (l >= ll && r <= rr) {
    add[x] += v;
    return;
