  int y = (l + r) >> 1;
  modify(x + x, l, y, ll, rr, v);
  modify(x + x + 1, y + 1, r, ll, rr, v);
  int wl = max[x + x] + add[x + x];
  int wr = max[x + x + 1] + add[x + x + 1];
  if (wl > wr) {
    max[x] = wl;
    index[x] = index[x + x];
  } else {
    max[x] = wr;
    index[x] = index[x + x + 1];
