using namespace std;

const int kMaxN = 20;
const double kPi = acos(-1.0);
const double kEps = 1E-8;
double f[1 << kMaxN];
double agl[kMaxN];
int n, l, r;

inline int sgn(double x) {
    if (fabs(x) < kEps) return 0;
    if (x > 0) return 1;
    return -1;
}

struct Point {
    double x, y;
    Point() {}
    Point(double x_, double y_) : x(x_), y(y_) {}
} p[kMaxN];

typedef Point Vector;

Vector operator-(const Vector &a, const Vector &b) {
    return Vector(a.x - b.x, a.y - b.y);
}

Vector rotate(const Vector &v, double agl) {
    return Vector(v.x * cos(agl) - v.y * sin(agl),
            v.x * sin(agl) + v.y * cos(agl));
}

double calcMaxDist(double from, const Point &p, double agl) {
    if (sgn(p.y) == 0) {
        if (sgn(from - p.x) < 0) return p.x;
        else return 1E100;
    }
    else {
        Vector a = Point(from, 0) - p;
        if (sgn(p.y) > 0) a = rotate(a, agl);
        else a = rotate(a, 2.0 * kPi - agl);
        if (sgn(a.y) == 0) return 1E100;
        if (sgn(p.y) > 0 && sgn(a.y) > 0) return 1E100;
        if (sgn(p.y) < 0 && sgn(a.y) < 0) return 1E100;
        if (sgn(a.x) == 0) return p.x;
        else {
            double k = a.y / a.x;
            double b = p.y - p.x * k;
            return (-b) / k;
        }
    }
}

int main() {
    scanf("%d%d%d", &n, &l, &r);
    for (int i = 0; i < (1 << n); ++ i) f[i] = l;
    for (int i = 0, a; i < n; ++ i) {
        scanf("%lf%lf%d", &p[i].x, &p[i].y, &a);
        agl[i] = a / 180.0 * kPi;
    }
    int upper = 1 << n;
    for (int msk = 0; msk < upper; ++ msk) {
        int can = upper - 1 - msk;
        while (can) {
            double current = f[msk];
            int q = can & -can;
            can ^= q;
            q = __builtin_ctz(q);
            current = max(current, calcMaxDist(current, p[q], agl[q]));
            current = min(current, (double)r);
            f[msk ^ (1 << q)] = max(f[msk ^ (1 << q)], current);
        }
    }
    printf("%.8lf\n", f[upper - 1] - l);
    return 0;
}
