        if (f[x] == f[f[x]]) {
            return f[x];
        }
        auto y = self(self, f[x]);
        val[x] += val[f[x]];
        f[x] = y;
        return f[x];
    };

    auto get = [&](i64 x) {
        find(find, x);
        i64 ans = val[x];
        if (x != f[x]) {
            ans += val[f[x]];
        }
        return ans;
    };

