struct tree{
	int n;
	vector<vector<int>> G;
	tree(){}
	tree(int n_){
		n=n_;
		G=vector<vector<int>>(n);
	}

	void add_edge(int a,int b){
		G[a].push_back(b);
		G[b].push_back(a);
	}
	void input(int ind){
		scanf("%d",&n);
		G=vector<vector<int>>(n);
		for(int i=0;i<n-1;i++){
			int a,b;
			scanf("%d%d",&a,&b);
			a-=ind; b-=ind;
			add_edge(a,b);
		}
	}

	void dfs_dist(int v,int p,int d,vector<int> &dist){
		dist[v]=d;
		for(int u: G[v]){
			if(u==p)continue;
			dfs_dist(u,v,d+1,dist);
		}
	}

	//centroids
	int cent;
	vector<tree*> subtree;
	vector<pair<int,int>> id;
	int dfs_sz(int v,int p,vector<int> &par,vector<int> &sz){
		par[v]=p;
		sz[v]=1;
		for(int u: G[v]){
			if(u==p)continue;
			sz[v]+=dfs_sz(u,v,par,sz);
		}
		return sz[v];
	}
	int dfs_id(int v,int p,pair<int,int> id_){
		id[v]=id_; id_.sc++;
		for(int u: G[v]){
			if(u==p)continue;
			id_.sc=dfs_id(u,v,id_);
			subtree[id_.fr]->add_edge(id[v].sc,id[u].sc);
		}
		return id_.sc;
	}
	void cent_div(){
		vector<int> sz(n),par(n);
		dfs_sz(0,-1,par,sz);
		cent=0;
		while(1){
			if(sz[cent]<=n/2){
				cent=par[cent];
				continue;
			}
			bool upd=false;
			for(int u: G[cent]){
				if(u==par[cent])continue;
				if(sz[u]>n/2){
					cent=u;
					upd=true;
					break;
				}
			}
			if(!upd)break;
		}
		id=vector<pair<int,int>>(n);
		for(int u: G[cent]){
			subtree.push_back(new tree(u==par[cent]?(n-sz[cent]):sz[u]));
			dfs_id(u,cent,mp(subtree.size()-1,0));
		}
		for(auto t: subtree)t->cent_div();
