/**
 * heap
 */

struct Node
{
        bool flag;
        int v, l, r, ll, rr;
        Node()
        {
        }
        Node(bool flag, int v, int l, int r, int ll, int rr) :
            flag(flag), v(v), l(l), r(r), ll(ll), rr(rr)
        {
        }
} h[MAX];
int K;

void sink(int k)
{
    while ((k << 1) <= K)
    {
        int maxv = max(h[k].v, h[k << 1].v);
        if ((k << 1 | 1) <= K)
        {
            maxv = max(maxv, h[k << 1 | 1].v);
        }
        if (maxv == h[k].v)
            break;
        else if (maxv == h[k << 1].v)
        {
            swap(h[k], h[k << 1]);
            k = k << 1;
        }
        else
        {
            swap(h[k], h[k << 1 | 1]);
            k = k << 1 | 1;
        }
    }
}

void flow(int k)
{
    while (k > 1)
    {
        int p = k >> 1;
        if (h[p].v < h[k].v)
        {
            swap(h[p], h[k]);
            k >>= 1;
        }
        else
        {
            break;
        }
    }
}

Node getMax()
{
    swap(h[1], h[K]);
    K--;
    sink(1);
    return h[K + 1];
}

void add(const Node& node)
{
    h[++K] = node;
    flow(K);
}

