		t[u].sum=t[ls].sum+t[rs].sum;
	}
	inline void pushdown(int u){
		if(~t[u].cov){
			t[ls].Cover(t[u].cov);
			t[rs].Cover(t[u].cov);
			t[u].cov=-1;
		}
	}
	void build(int u,int L,int R){
		t[u].len=R-L+1;
		if(L==R)return;
		int mid=(L+R)>>1;
		build(ls,L,mid);
		build(rs,mid+1,R);
	}
	void _cover(int u,int L,int R,int l,int r,int d){
		if(L>=l&&R<=r){
			t[u].Cover(d);
			return;
		}
		pushdown(u);
		int mid=(L+R)>>1;
		if(l<=mid)_cover(ls,L,mid,l,r,d);
		if(r>mid)_cover(rs,mid+1,R,l,r,d);
		update(u);
	}
	vector<tuple<int,int,int> > a;
	void _get(int u,int L,int R,int l,int r){
		if(L>=l&&R<=r){
			a.emplace_back(u,L,R);
			return;
		}
		pushdown(u);
		int mid=(L+R)>>1;
		if(l<=mid)_get(ls,L,mid,l,r);
		if(r>mid)_get(rs,mid+1,R,l,r);
	}
	int _find_0(int u,int L,int R){
		if(t[u].sum==t[u].len)return -1;
		if(L==R)return L;
		pushdown(u);
		int mid=(L+R)>>1;
		if(t[ls].len!=t[ls].sum)return _find_0(ls,L,mid);
		return _find_0(rs,mid+1,R);
	}
	int _find_1(int u,int L,int R){
		if(0==t[u].sum)return -1;
		if(L==R)return L;
		pushdown(u);
		int mid=(L+R)>>1;
		if(0!=t[ls].sum)return _find_1(ls,L,mid);
		return _find_1(rs,mid+1,R);
	}
	int _gg(int u,int L,int R){
		if(0==t[u].len)return -1;
		if(L==R)return L;
		pushdown(u);
		int mid=(L+R)>>1;
		if(0!=t[rs].sum)return _gg(rs,mid+1,R);
		return _gg(ls,L,mid);
	}
public:
	void init(int _n){
		Len=_n;
		build(1,1,Len);
	}
	int Find_0(int lim){
		a.clear();
		_get(1,1,Len,lim,Len);
		for(auto [u,L,R]:a){
			int z=_find_0(u,L,R);
			if(~z)return z;
		}
		assert(false);
	}
	int Find_1(int lim){
		a.clear();
		_get(1,1,Len,lim,Len);
		for(auto [u,L,R]:a){
			int z=_find_1(u,L,R);
			if(~z)return z;
		}
		assert(false);
	}
	void Add(int x){
		int y=Find_0(x);
		if(y>x)_cover(1,1,Len,x,y-1,0);
		_cover(1,1,Len,y,y,1);
	}
	void Del(int x){
		int y=Find_1(x);
		if(y>x)_cover(1,1,Len,x,y-1,1);
		_cover(1,1,Len,y,y,0);
	}
	int get_ans(){
		return _gg(1,1,Len);
	}
}T;
int main(){
	ios::sync_with_stdio(false);
	cin.tie(0);
	T.init(200200);
	cin>>n>>Q;
	for(int i=1;i<=n;++i){
		cin>>a[i];
