ll binpow(ll a, ll k) {
	if (k == 0) return 1;
	ll ans = binpow(a, k / 2);
	ans = 1ll * ans * ans % mod;
	if (k % 2) ans = 1ll * ans * a % mod;
	return ans;
}

ll g(ll n, ll k) {
	if (k < mod) {
		ll t = binpow(2, n);
		ll ans = 1;
		for (int i = 0; i <= k; i++) {
			ans *= (t - (2ll * i + 1));
			ans %= mod;
			if (ans < 0) ans += mod;
		}
		return ans;
	}
	ll t = binpow(2, n);
	ll cur = 1;
	for (int i = 0; i < mod; i++) {
		cur *= (t - (2ll * i + 1));
		cur %= mod;
		if (cur < 0) cur += mod;
	}
	ll s = k / mod;
	ll ans = binpow(cur, s) * g(n, k % mod) % mod;
	return ans;
}

ll f(ll n, ll k) {
	if (k == 0) return 1;
	ll t = (k - 1) / 2;
	ll ans = g(n, t);
	return ans * 1ll * f(n - 1, k / 2) % mod;
}

