    if (v > p.first) {
      p.second = p.first;
      p.first = v;
    } else {
      p.second = max(p.second, v);
    }
  };
  for (int i = 0; i < n; i++) {
    Update(bests[a[i]], i);
  }
  for (int j = 0; j < BITS; j++) {
    for (int i = 0; i < MAX; i++) {
      if (i & (1 << j)) {
        Update(bests[i ^ (1 << j)], bests[i].first);
        Update(bests[i ^ (1 << j)], bests[i].second);
      }
    }
  }
  auto Can = [&](int num, int high) {
