/**
 * Description: Barrett reduction computes $a \% b$ about 4 times faster than
 * usual where $b>1$ is constant but not known at compile time. Division by $b$
 * is replaced by multiplication by $m$ and shifting right 64 bits. Source:
 * KACTL
 * https://github.com/kth-competitive-programming/kactl/blob/master/content/various/FastMod.h
 * https://en.wikipedia.org/wiki/Barrett_reduction
 * Verification: http://www.usaco.org/index.php?page=viewproblem2&cpid=1045
 */

using ul = uint64_t;
using L = __uint128_t;
struct FastMod {
	ul b, m;
	FastMod(ul b) : b(b), m(-1ULL / b) {}
	ul reduce(ul a) {
		ul q = (ul)((__uint128_t(m) * a) >> 64), r = a - q * b;
		return r - (r >= b) * b;
	}
};

/**
 * Description: modular arithmetic operations
 * Source:
 * KACTL
 * https://codeforces.com/blog/entry/63903
 * https://codeforces.com/contest/1261/submission/65632855 (tourist)
 * https://codeforces.com/contest/1264/submission/66344993 (ksun)
 * also see https://github.com/ecnerwala/cp-book/blob/master/src/modnum.hpp
 * (ecnerwal)
 * https://github.com/atcoder/ac-library/blob/master/atcoder/modint.hpp
 * Verification:
 * https://open.kattis.com/problems/modulararithmetic
 */

template <int id> struct mint {
	static FastMod MF;
	static int mod() { return (int)MF.b; }
	static void set_mod(int m) {
		assert(1 <= m);
		MF = FastMod(m);
	}
	int v;
	explicit operator int() const {
		return v;
	} // explicit -> don't silently convert to int
	mint() { v = 0; }
	mint(ll _v) {
		v = int((-mod() < _v && _v < mod()) ? _v : _v % mod());
		if (v < 0) v += mod();
	}
	friend bool operator==(const mint &a, const mint &b) { return a.v == b.v; }
	friend bool operator!=(const mint &a, const mint &b) { return !(a == b); }
	friend bool operator<(const mint &a, const mint &b) { return a.v < b.v; }
	friend void re(mint &a) {
		ll x;
		re(x);
		a = mint(x);
	}
	friend str ts(mint a) { return ts(a.v); }

	mint &operator+=(const mint &m) {
		if ((v += m.v) >= mod()) v -= mod();
		return *this;
	}
	mint &operator-=(const mint &m) {
		if ((v -= m.v) < 0) v += mod();
		return *this;
	}
	mint &operator*=(const mint &m) {
		v = (int)MF.reduce((ll)v * m.v);
		return *this;
	}
	mint &operator/=(const mint &m) { return (*this) *= inv(m); }
	friend mint pow(mint a, ll p) {
		mint ans = 1;
		assert(p >= 0);
		for (; p; p /= 2, a *= a)
			if (p & 1) ans *= a;
		return ans;
	}
	friend mint inv(const mint &a) {
		assert(a.v != 0);
		return pow(a, mod() - 2);
	}

	mint operator-() const { return mint(-v); }
	mint &operator++() { return *this += 1; }
	mint &operator--() { return *this -= 1; }
	friend mint operator+(mint a, const mint &b) { return a += b; }
	friend mint operator-(mint a, const mint &b) { return a -= b; }
	friend mint operator*(mint a, const mint &b) { return a *= b; }
	friend mint operator/(mint a, const mint &b) { return a /= b; }
};
template <int id> FastMod mint<id>::MF = FastMod(1);

