	{
		while (y) x=p[x][lg2[y&-y]], y-=y&-y;
		return x;
	}

	int lca(int x, int y)
	{
		if (depth[x]>depth[y]) x=moveup(x,depth[x]-depth[y]); else y=moveup(y,depth[y]-depth[x]);
		repd(i,19,0)
			if (p[x][i]!=p[y][i])
			{
				x=p[x][i]; y=p[y][i];
			}
		if (x==y) return x; else return p[x][0];
	}

	void readin(int _n, int *d, int *od)
	{
