    cin >> a[i];
    a[i]--;
    q[a[i]].insert(i);
    cnt[a[i]]++;
    //add(a[i], i);
  }
  for (int i = 0; i < N; i++)  {
    if (!q[i].empty()) {
      vis[*q[i].begin()] = true;
      w[*q[i].begin()] = cnt[i];
      tr[*q[i].begin()] = *q[i].rbegin();
      e.insert({*q[i].begin(), *q[i].rbegin(), cnt[i]});
    }
  }
  for (int i = 0; i < N; i += BL) {
    rebuild_block(i);
  }
  cout << n - ans() << '\n';
  for (int j = 0; j < tq; j++) {
    int i, x;
    cin >> i >> x;
    i--, x--;
    del(a[i], i);
    a[i] = x;
    add(a[i], i);
    cout << n - ans() << '\n';
  }
}
