				seg.ch(ban,hld.dep[p],&N::add,V);
			}
			seg.point_change(hld.dep[p],&N::add,-pass[v]);
		}
		int hc=si(t[v])?hld.g[v][0]:-1;
		for(auto ch:hld.g[v]){
			if(ch!=hc){
				self(self,ch);
