			if(cov[v]<hld.dep[p]){
				ll val=seg.composite(cov[v],hld.dep[p]).mn;
				ll unko=pass[v]-val;
				if(ans<unko){
					int pos=seg.max_right(cov[v],&N::ok,val).a;
