			}
			seg.point_change(hld.dep[p],&N::add,pass[v]);
			ebuf.pop_back();
		}
		if(hld.head[v]==v){
			sub(sub,v,-1);
		}
	};
	dfs(dfs,0);

	print(ans);
	print_offset(e1,1);
	print_offset(e2,1);
