tuple<bool, int, int> domerge(tuple<bool, int, int> a, tuple<bool, int, int> b) {
	return {
		get<0>(a) && get<0>(b) && get<2>(a) > get<1>(b),
		max(get<1>(a), get<1>(b)),
		min(get<2>(a), get<2>(b))
	};
}


class SegmentTree {
public:
	vector< tuple<bool, int, int> > a;
	int n;

	SegmentTree(int sz) {
		n = sz;
		a.resize(4 * n, tuple<bool, int, int>(true, -1, 1000000000LL));
	}

	void set(int v, int vl, int vr, int i, tuple<bool, int, int> x) {
		if(vr - vl == 1) {
			a[v] = x;
		} else {
			int vm = (vl + vr) / 2;
			if(i < vm) {
				set(v * 2, vl, vm, i, x);
			} else {
				set(v * 2 + 1, vm, vr, i, x);
			}
			a[v] = domerge(a[v * 2], a[v * 2 + 1]);
		}
	}

	void set(int i, tuple<bool, int, int> x) {
		set(1, 0, n, i, x);
	}

	tuple<bool, int, int> get(int v, int vl, int vr, int l, int r) {
		if(vl == l && vr == r) {
			return a[v];
		} else {
			int vm = (vl + vr) / 2;
			if(r <= vm) {
				return get(v * 2, vl, vm, l, r);
			} else if(l >= vm) {
				return get(v * 2 + 1, vm, vr, l, r);
			} else {
				return domerge(get(v * 2, vl, vm, l, vm), get(v * 2 + 1, vm, vr, vm, r));
			}
		}
	}

	tuple<bool, int, int> get(int l, int r) {
		return get(1, 0, n, l, r);
	}
};

signed main() {
	int n, m, q;
	cin >> n >> m >> q;

	SegmentTree st(n);

	for(int cnt = 0; cnt < q; cnt++) {
		int i, j;
		cin >> i >> j;
		i--; j--;
		bool is_one = i % 2 == 0;
		i /= 2;
