vector<int> e[maxn];

int up[LG][maxn], sz[maxn], h[maxn];

pair<int, int> seg[maxn];

int tnow;

void dfs(int v = 0, int p = -1) {
    sz[v] = 1;
    seg[v].fr = tnow++;
    for (int i : e[v])
        if (i != p) {
            h[i] = h[v] + 1;
            dfs(i, v);
            sz[v] += sz[i];
            up[0][i] = v;
        }
    seg[v].sc = tnow - 1;
}

// v anc of u

bool isanc(int v, int u) {
    return seg[v].fr <= seg[u].fr && seg[v].sc >= seg[u].sc;
}

int climb(int v, int t) {
    for (int i = 0; i < LG; i++)
        if ((1 << i) & t)
            v = up[i][v];
    return v;
}

int get_lca(int v, int u) {
    if (h[v] < h[u])
        swap(v, u);
    v = climb(v, h[v] - h[u]);
    if (v == u)
        return v;
    for (int i = LG - 1; i >= 0; i--)
        if (up[i][v] != up[i][u])
            v = up[i][v], u = up[i][u];
    return up[0][v];
}

int n;

void solve() {
    tnow = 0;
    cin >> n;
    for (int i = 0; i < n; i++)
        e[i].clear();
    for (int i = 1; i < n; i++) {
        int v, u;
        cin >> v >> u;
        e[v].push_back(u);
        e[u].push_back(v);
    }
    dfs();
    for (int j = 1; j < LG; j++)
        for (int i = 0; i < n; i++)
            up[j][i] = up[j - 1][up[j - 1][i]];

    vector<ll> dp(n + 2);
    dp[0] = n * (ll)(n - 1) / 2;
    dp[1] = dp[0];
    for (int i : e[0])
        dp[1] -= sz[i] * (ll)(sz[i] - 1) / 2;

    int v = 0, u = 1;
    for (int it = 2; it <= n; it++) {
        cerr << "at " << it - 1 << " " << v << ' ' << u << '\n';
        if (h[v] < h[u])
            swap(v, u);
        if (isanc(u, v)) {
            dp[it] = sz[v] * (ll)(n - sz[climb(v, h[v] - h[u] - 1)]);
        } else {
            dp[it] = sz[v] * (ll)sz[u];
        }
        if (it == n)
            break;

        int lca = get_lca(v, u);
        if (isanc(lca, it) && (isanc(it, v) || isanc(it, u)))
            continue;

        if (isanc(u, v)) {
            if (isanc(v, it)) {
                v = it;
                continue;
            }
            if (isanc(climb(v, h[v] - h[u] - 1), it)) {
                break;
            }
            u = it;
            continue;
        }
        if (isanc(v, it)) {
            v = it;
            continue;
        }
        if (isanc(u, it)) {
            u = it;
            continue;
        }
        break;
    }
    for (int i = 0; i <= n; i++)
        cout << dp[i] - dp[i + 1] << ' ';
    cout << '\n';
