int pointer;
struct Node {
	Node*ch[CHARSET], *fail, *par;
	Node*jump[CHARSET];

	int end;
	Node() {
		memset(ch, 0, sizeof ch);
		fail = 0;
		end = 0;
	}
	Node*go(int w);
}*root;

Node nodePool[MAX_N_NODES], *cur;
const int MAX_ID = 400 + 10;

int getId(Node*u) {
	return u - nodePool;
}

Node*newNode() {
	Node*t = cur++;
	memset(t->ch, 0, sizeof t->ch);
	t->fail = 0;
	return t;
}

Node* Node::go(int w) {
	if (ch[w] == 0) {
		ch[w] = newNode();
		ch[w]->par = this;
	}
	return ch[w];
}

void init() {
	cur = nodePool;
	root = newNode();
	root->par = 0;
}

void build() {
	static Node*que[MAX_N_NODES];
	int qh = 0, qt = 0;
	que[qt++] = root;
	while (qh < qt) {
		Node*t = que[qh++];
		for (int c = 0; c < CHARSET; ++c) {
			Node*v = t->ch[c];
			if (!v)
				continue;
			Node*f = t->fail;
			while (f && f->ch[c] == 0)
				f = f->fail;
			if (f == 0)
				v->fail = root;
			else
				v->fail = f->ch[c];
			que[qt++] = v;
		}
	}
	//calculate JUMP
	for (int j = 0; j < m; ++j) {
		root->jump[j] = root->ch[j] ? root->ch[j] : root;
//		if (root->jump[j] == 0)
//			root->jump[j] = root;
	}

	for (int i = 1; i < qt; ++i) {
		Node*u = que[i];
		for (int j = 0; j < m; ++j) {
			if (u->ch[j])
				u->jump[j] = u->ch[j];
			else
				u->jump[j] = u->fail->jump[j];
		}
	}

	for (int i = 0; i < qt; ++i) {
		Node*u = que[i];
		if (u->fail)
			u->end += u->fail->end;
	}
}

