	have += u->end;
	if (have > k)
		return 0;

	if (at == L.size()) {
		return 1;
	}

	bool save = !(sameL || sameR);
	int ret = 0;
	if (save) {
		ret = memo[at][getId(u)][have];
		if (ret != -1)
			return ret;
		ret = 0;
	}

	for (int nxt = 0; nxt < m; ++nxt) { //nxt digit
		if (sameL && nxt < L[at])
			continue;
		if (sameR && nxt > R[at])
			continue;
		Node*nu = u;
		if (!leadingZero || nxt > 0)
			nu = u->jump[nxt];

		ret += rec(at + 1, leadingZero && (nxt == 0), sameL && (nxt == L[at]),
				sameR && (nxt == R[at]), nu, have);
		if (ret >= MOD)
			ret -= MOD;
	}

	if (save) {
		memo[at][getId(u)][have] = ret;
	}

	return ret;
}

int main() {
