long long pw(long long a, long long b) {
  long long x = 1, step = 1LL << 60;
  while (step > 0) {
    x = x * x % md;
    if (step & b) x = x * a % md;
    step >>= 1;
  }
  return x;
}

int main() {
  long long n, m, k;
  cin >> n >> m >> k;
  long long ll = 0, rr = m / k;
  while (ll < rr) {
