			deg[i] = 0;
		}
		vector<pair<int, int> > result;
		for (auto p : answer) {
			int a = p.first, b = p.second;
			if (baned.count({a, b}) || baned.count({b, a})) {
				baned.erase({min(a, b), max(a, b)});
