	node *r;
	int sm;
};

typedef node* pnode;


const int MAXN = 600020;

int sz[MAXN];	
vector <int> e[MAXN];
int p[MAXN];
int l[MAXN], r[MAXN];
int tm = 0;
pnode root[MAXN];
int n;
int ans[MAXN];

pnode create_rmq(int cn)
{
	pnode cur = new node();
	cur->sm = 0;
	if (cn == 1)
	{
		cur->l = cur->r = NULL;
	}
	else cur->l = create_rmq((cn + 1) / 2), cur->r = create_rmq(cn / 2);
	return cur;
}

int lv, rv = 1;

pnode modify_rmq(pnode t, int l, int r)
{
	pnode an = new node();
	an->sm = t->sm + rv;
	if (l == r)
	{
		an->l = an->r = NULL;
	}
	else
	{
		int md = (l + r) / 2;
		if (lv <= md) an->l = modify_rmq(t->l, l, md), an->r = t->r;
		else an->l = t->l, an->r = modify_rmq(t->r, md + 1, r);
	}
	return an;
}

void modify_rmq2(pnode t, int l, int r)
{
	t->sm += rv;
	if (l != r)
	{
		int md = (l + r) / 2;
		if (lv <= md) modify_rmq2(t->l, l, md);
		else modify_rmq2(t->r, md + 1, r);
	}
}

typedef vector <pair <pnode, int> > vectt;


vectt vts[MAXN];

bool go_rmq(int l, int r, int level = 0)
{
	int vc = vts[0].size();
	if ((lv <= l) && (r <= rv))
	{
		int cur = 0;
		for (int i = 0; i < vc; i++)
			cur += (vts[level][i].first -> sm) * vts[level][i].second;
		return (cur > 0);
	}
	int md = (l + r) / 2;
	vts[level + 1] = vts[level];
	if (lv <= md)
	{
		for (int i = 0; i < vc; i++)
		{
			vts[level + 1][i].first = vts[level][i].first -> l;
		}
		if (go_rmq(l, md, level + 1)) return true;
	}
	if (md < rv)
	{
		for (int i = 0; i < vc; i++)
		{
			vts[level + 1][i].first = vts[level][i].first -> r;
		}
		if (go_rmq(md + 1, r, level + 1)) return true;
	}
	return false;
}

void dfs1(int v)
{
	l[v] = tm;
	sz[v] = 1;
	for (int i = 0; i < e[v].size(); i++)
	{
		dfs1(e[v][i]);
		sz[v] += sz[e[v][i]];
	}
	r[v] = ++tm;
	lv = sz[v];
	rv = 1;
	root[tm] = modify_rmq(root[tm - 1], 0, n);
}

pnode del;

int rt;

vector <pair <int, int> > cur;

void dfs2(int v)
{
	cur.clear();
	for (int i = 0; i < e[v].size(); i++)
	{
		cur.push_back(mp(sz[e[v][i]], e[v][i]));
	}
	if (v != rt) cur.push_back(mp(n - sz[v], 0));
	sort(cur.begin(), cur.end());
	int cs = cur.size();
	if (cs == 1) ans[v] = n - 1;
	else
	{
		vectt vt;
		vt.clear();
		vectt vt2;
		vt2.clear();
		vt2.push_back(mp(del, 1));
		int ls = cur[cs - 1].second;
		if (ls == 0)
		{
			vt.push_back(mp(root[l[v]], 1));
			vt.push_back(mp(root[r[v]], -1));
			vt.push_back(mp(root[n], 1));
			vt.push_back(mp(del, -1));
		}
		else
		{
			vt.push_back(mp(root[l[ls]], -1));
			vt.push_back(mp(root[r[ls]], 1));
		}
		int lg = cur[cs - 2].first, rg = cur[cs - 1].first;
		while (lg < rg)
		{
			int md = (lg + rg) / 2;
			lv = cur[cs - 1].first - md;
			rv = md - cur[0].first;
			if (lv > rv)
			{
				lg = md + 1;
				continue;
			}
			vts[0] = vt;
			bool ok = go_rmq(0, n);
			if (ls == 0)
			{
				lv += sz[v];
				rv += sz[v];
				rv = min(rv, n);
				if (lv <= rv)
				{
					vts[0] = vt2;
					ok |= go_rmq(0, n);
				}
			}
			if (ok) rg= md;
			else lg = md + 1;
		}
		ans[v] = lg;
	}
	lv = sz[v], rv = 1;
	modify_rmq2(del, 0, n);
	for (int i = 0; i < e[v].size(); i++)
	{
		dfs2(e[v][i]);
	}
	lv = sz[v], rv = -1;
	modify_rmq2(del, 0, n);
}



int main()
{
	ios_base::sync_with_stdio(0);	
	cin >> n;
	if (n == 1)
	{
		cout << 0;
		return 0;
	}
	for (int i = 1; i <=  n; i++)
	{
		int u, v;
		cin >> u >> v;
		if (u == 0)
		{
			p[v] = v;
			rt = v;
		}
		else
		{
			p[v] = u;
			e[u].push_back(v);
		}
	}
	root[0] = create_rmq(n + 1);
	del = create_rmq(n + 1);
	dfs1(rt);
	dfs2(rt);
	for (int i = 1; i <= n; i++) cout << ans[i] << " ";
	cout << "\n";
	return 0;
}


