  vector<int> l(n), r(n);
  vector<long long> cost(n);
  long long total = 0;
  for (int i = 0; i < n; i++) {
    int x, y;
    cin >> x >> y >> cost[i];
    l[i] = x;
    r[i] = k - y;
    total += cost[i];
  }
  vector<vector<int>> at(k + 1);
  for (int i = 0; i < n; i++) {
    at[r[i]].push_back(i);
  }
  segtree st(k + 1);
  long long dp = 0;
  for (int i = 1; i <= k; i++) {
    for (int id : at[i]) {
      st.modify(0, l[id], +cost[id]);
    }
    dp = max(dp, -A * i + st.get(0, i - 1).mx);
    st.modify(i, i, dp + A * i);
  }
  cout << total - dp << '\n';
  return 0;
}
