struct LAZY{
vector<ll> tree,lazy;
int n;
LAZY(){}
LAZY(int _n){ // pass a vector/array if needed
  n = _n;
  tree.resize(4*n + 100,0);
  lazy.resize(4*n + 100,0);
}

ll f(ll a,ll b){
   return (a>b ? a : b); // max/min (a>b ? a : b)
}
void build(int no,int i,int j){
  if(i==j){
    tree[no] = 0;
  }else{
    int m = (i+j)/2;
    int l = 2*no,r=2*no+1;
    build(l,i,m);
    build(r,m+1,j);
    tree[no] = f(tree[l],tree[r]);
  }
}

void propagate(int no,int i,int j){
  if(lazy[no]==0)return;
  tree[no]+=lazy[no];
  if(i!=j){
    lazy[2*no]+=lazy[no];
    lazy[2*no+1]+=lazy[no];
  }
  lazy[no] = 0;
}

void update(int no,int i,int j,int a,int b,ll v){
  propagate(no,i,j);
  if(i>b || j<a || i>j)return;
  if(a<=i && j<=b){
    tree[no] += v;
    if(i!=j){
      lazy[2*no]+=v;
      lazy[2*no+1]+=v;
    }
    return;
  }
  int m = (i+j)/2;
  int l = 2*no,r=2*no+1;
  update(l,i,m,a,b,v);
  update(r,m+1,j,a,b,v);  
  tree[no] = f(tree[l],tree[r]);
}

const ll inf = 1e18;
ll query(int no,int i,int j,int a,int b){
  if(i>b || j<a || i>j)return -inf;

  propagate(no,i,j);

  if(a<=i && j<=b)return tree[no];

  int m = (i+j)/2;
  int l = 2*no,r=2*no+1;

  return f(query(l,i,m,a,b),query(r,m+1,j,a,b));
}


