  int y;
  int c;
};

int main() {
  ios::sync_with_stdio(false);
  cin.tie(0);
  int n, k, L;
  cin >> n >> k >> L;
  vector<Point> p(n);
  for (int i = 0; i < n; i++) {
    cin >> p[i].x >> p[i].y >> p[i].c;
    --p[i].c;
  }
  sort(p.begin(), p.end(), [&](const Point& a, const Point& b) {
    return a.y < b.y;
  });
  long long c2 = (long long) L * (L + 1) / 2;
  auto C2 = [&](int x, int y) -> long long {
    if (x >= y) {
      return 0;
    }
    return ((long long) (y - x) * (y - x + 1)) >> 1;
  };
  Mint ans = 0;
  for (int start = 0; start <= n - k; start++) {
    int coeff = p[start].y - (start == 0 ? -1 : p[start - 1].y);
    if (coeff == 0) {
      continue;
    }
    vector<multiset<int>> s(k);
    for (int i = 0; i < k; i++) {
      s[i].insert(-1);
      s[i].insert(L);
    }
    for (int i = start; i < n; i++) {
      s[p[i].c].insert(p[i].x);
    }
    set<pair<int, int>> segs;
    long long value = 0;
    auto Modify = [&](set<pair<int, int>>::iterator it, int q) {
      if (it != segs.begin()) {
        value -= q * C2(it->first, abs(prev(it)->second));
      }
      if (next(it) != segs.end()) {
        value -= q * C2(next(it)->first, abs(it->second));
      }
      if (it != segs.begin() && next(it) != segs.end()) {
        value += q * C2(next(it)->first, abs(prev(it)->second));
      }
      value += q * C2(it->first, abs(it->second));
    };
    auto Erase = [&](set<pair<int, int>>::iterator it) {
      Modify(it, -1);
      segs.erase(it);
    };
    auto Add = [&](int from, int to) {
      if (from >= to) {
        return;
      }
      pair<int, int> seg = make_pair(from, -to);
      auto it = segs.lower_bound(seg);
      while (it != segs.end() && abs(it->second) <= to) {
        Erase(it);
        it = segs.lower_bound(seg);
      }
      if (it != segs.begin()) {
        it = prev(it);
        if (abs(it->second) >= to) {
          return;
        }
      }
      auto ret = segs.insert(seg);
      assert(ret.second);
      it = ret.first;
      Modify(it, 1);
    };
    bool have_all = true;
    for (int i = 0; i < k; i++) {
      if (s[i].size() == 2) {
        have_all = false;
        break;
      }
      auto it = s[i].begin();
      while (*it < L) {
        int me = *it;
        ++it;
        int nxt = *it;
        Add(me + 1, nxt);
      }
    }
    if (!have_all) {
      break;
    }
    for (int finish = n - 1; finish >= start; finish--) {
      int coeff2 = (finish == n - 1 ? L : p[finish + 1].y) - p[finish].y;
      ans += Mint(coeff) * coeff2 * (c2 - value);
      int col = p[finish].c;
      auto it = s[col].find(p[finish].x);
      assert(it != s[col].end());
      auto pr = prev(it);
      auto nx = next(it);
      Add((*pr) + 1, *nx);
      s[col].erase(it);
      if (value == c2) {
        break;
      }
    }
  }
  cout << ans << '\n';
  return 0;
}
