const int mod = 1e9 + 7;

int pw(int x, int p) {
    int r = 1;
    while (p) {
        if (p & 1) r = (llong)r * x % mod;
        x = (llong)x * x % mod;
        p >>= 1;
    }
    return r;
}

int popcount(int x) {
    int ret = 0;
    while (x) {
        ret += x & 1;
        x >>= 1;
    }
    return ret;
}

int m;
int dp[100001];
vector<int> di[100001];
vector<pii> pr[100001];
int prs[100001];
int main() {
    ios_base::sync_with_stdio(0); cin.tie(0);
    cin >> m;
    if (m == 1) {
        printf("1\n");
        return 0;
    }
    for (int i = 1; i <= m; ++i) {
        for (int j = i; j <= m; j += i) di[j].push_back(i);
        if (di[i].size() == 2) {
            for (int j = i; j <= m; j += i) {
                int k = j;
                pr[j].emplace_back(i, 0);
                while (k % i == 0) ++pr[j].back().second, k /= i;
            }
        }
    }
    dp[1] = 1;
    for (int i = 2; i <= m; ++i) {
        int ci, rhs = m;
        for (int j : di[i]) {
            for (pii k : pr[j]) prs[k.first] = k.second;
            vector<int> ps;
            int rm = m;
            for (pii k : pr[i]) {
                for (int l = prs[k.first]; l--; ) rm /= k.first;
                if (prs[k.first] < k.second) ps.push_back(k.first);
            }
            for (pii k : pr[j]) prs[k.first] = 0;
            int sum = 0;
            for (int k = 0; k < (1 << ps.size()); ++k) {
                int sign = popcount(k) & 1;
                sign = 1 - sign - sign;
                int nm = rm;
                for (int l = 0; l < ps.size(); ++l) if ((k >> l) & 1) nm /= ps[l];
                sum += sign * nm;
            }
            if (j == i) ci = m - sum;
            else rhs += (llong)sum * dp[j] % mod, rhs %= mod;
        }
        dp[i] = (llong)rhs * pw(ci, mod - 2) % mod;
    }
    int ans = 0;
    for (int i = 1; i <= m; ++i) ans += dp[i], ans %= mod;
    ans = (llong)ans * pw(m, mod - 2) % mod;
