namespace Splay{
	int cnt,root;
	struct tree{
		int f,val,siz,num;
		int son[2];
	}t[200005];
	inline void pushup(int x){
		t[x].siz=t[t[x].son[0]].siz+t[t[x].son[1]].siz+t[x].num;
	}
	inline void rot(int x){
		int prt=t[x].f,grf=t[prt].f,key=(t[prt].son[1]==x);
		t[grf].son[t[grf].son[1]==prt]=x;
		t[x].f=grf;
		t[prt].son[key]=t[x].son[!key];
		t[t[x].son[!key]].f=prt;
		t[x].son[!key]=prt;
		t[prt].f=x;
		pushup(prt),pushup(x);
	}
	inline void splay(int x,int goal){
		while(t[x].f!=goal){
			int prt=t[x].f,grf=t[t[x].f].f;
			if(grf!=goal&&(t[grf].son[1]==prt)==(t[t[x].f].son[1]==x))rot(t[x].f);
			rot(x);
		}
		if(!goal)root=x;
	}
	inline void insert(int x){
		int y=root,prt=0;
		while(y&&t[y].val!=x)prt=y,y=t[y].son[x>t[y].val];
		if(y)++t[y].num;
		else{
			y=++cnt;
			if(prt)t[prt].son[x>t[prt].val]=y;
			t[y].son[0]=t[y].son[1]=0;
			t[y].val=x,t[y].f=prt,t[y].num=t[y].siz=1;
		}
		splay(y,0);
	}
	inline void find(int x){
		if(!root)return;
		int y=root;
		while(t[y].son[x>t[y].val]&&x!=t[y].val)y=t[y].son[x>t[y].val];
		splay(y,0);
	}
	inline int fhd(int x,int key){
		find(x);
		if(t[root].val>x&&key)return root;
		if(t[root].val<x&&!key)return root;
		int y=t[root].son[key];
		if(!y)return 0;
		while(t[y].son[!key])y=t[y].son[!key];
		return y;
	}
	inline void delt(int x){
		int fro=fhd(x,0),beh=fhd(x,1);
		splay(fro,0),splay(beh,fro);
		int y=t[beh].son[0];
		if(t[y].num>1)--t[y].num,splay(y,0);
		else t[beh].son[0]=0,splay(beh,0);
	}
	inline int kth(int x){
		if(t[root].siz<x)return -1;
		int y=root;
		while(1){
			int s=t[y].son[0];
			if(x<=t[s].siz)y=s;
			else if(x<=t[y].num+t[s].siz)return t[y].val;
			else x-=(t[s].siz+t[y].num),y=t[y].son[1];
		}
	}
}
